targets.expr:
topk(5, sum by (schema) (
    label_replace(
      rate(polardbx_stc_request_time_cost_total{app_kubernetes_io_instance="$polardbx", namespace="$namespace", appname!~"__.*__@.*|information_schema@.*|polardbx@.*"}[1m]),
      "schema",
      "$1",
      "appname",
      "(.*)@.*"
    )
  )
) by (schema)

